import { Injectable } from '@angular/core';
import {HttpClient, HttpHeaders} from '@angular/common/http';
import {Observable} from 'rxjs';
import { User } from '../interface/user';

@Injectable({
  providedIn: 'root'
})

export class UserService {

  private apiUrl ='https://jsonplaceholder.typicode.com/users';

  constructor(private http: HttpClient) { }

  getUsers(): Observable <User[]> { //paczy na Usera czy się zmienia, zwraca <get> tablice z użytkownikami
    const myHeaders= new HttpHeaders({'myeader': 'headervalue'}); //headery daje
    return this.http.get<User[]>(`${this.apiUrl}`)  
  }

  getUser(): Observable<User> {
    return this.http.get<User>(`${this.apiUrl}/1`)
  }

  createUser(user: User): Observable<User> {
    return this.http.post<User>(`${this.apiUrl}`, user)
  } 

  updateUser(user: User): Observable<User> {
        //put musisz dać wszystkie wartości bo reszta będzie domyślna
       // różni się w zależności od api
    return this.http.put<User>(`${this.apiUrl}/${user.id}`, user)
  }

  patchUser(user: User): Observable<User> {
    //patch nie  musisz dać wszystkie wartości
    return this.http.patch<User>(`${this.apiUrl}/${user.id}`, user)
  }

  deleteUser(id: number): Observable<void> {
    //delete daje ci najczęściej boolean^
    //void bo nic nie dostajemy w odpowiedzi od servera 
    return this.http.delete<void>(`${this.apiUrl}/${id}`)
  }
}
