import { Component, OnInit } from '@angular/core';
import { UserService } from './service/user.service';
import { User } from './interface/user';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'angularhttp';

  private user: User = {
    'id': 10,
    'name': 'Lmao Graham',
    'username': 'Lmao',
    'email': 'pierre@april.biz',
    'address': {
      'street': 'Kulas Light',
      'suite': 'Apt. 556',
      'city': 'Gwenborough',
      'zipcode': '92998-3874',
      'geo': {
        'lat': '-37.3159',
        'lng': '81.1496'
      }
    },
    'phone': '1-770-736-8031 x56442',
    'website': 'hildegard.org',
    'company': {
      'name': 'pierrrrr',
      'catchPhrase': 'Multi-layered client-server neural-net',
      'bs': 'harness real-time e-markets'
    }
  }
  private user1: any = {
    'id': 2,
    'name': 'teast Graham',
    'username': 'Lmao',
    'email': 'pierre@april.biz',
  }

  constructor(private userService: UserService) { }

  ngOnInit(): void {
    //this.onUpdateUser();
    //this.onPatchUser();
    //this.onGetUsers();
    //this.onGetUsers2();
    this.onGetUsers3();
    //this.onGetUser();
    //this.onCreateUser();
    //this.onDeleteUser();
    //this.onTextFile();
  }
  onGetUsers(): void {
    this.userService.getUsers().subscribe(
      (response) => console.table(response),
      (error: any) => console.log(error),
      () => console.log('Done getting users')
    )
  }
  onGetUsers2(): void {
    this.userService.getUsers2().subscribe(
      (response) => console.log(response),
      (error: any) => console.log(error),
      () => console.log('Done getting users')
    )
  }
  onGetUsers3(): void {
    this.userService.getUsers3().subscribe(
      (response) => console.log(response),
      (error: any) => console.log(error),
      () => console.log('Done getting users')
    )
  }

  onTextFile(): void {
    this.userService.getTextFile().subscribe(
      (response) => console.log('Response:', response),
      (error: any) => console.log(error),
      () => console.log('Done getting files')
    )
  }

  onGetUser(): void {
    this.userService.getUser().subscribe(
      (response) => console.log(response),
      (error: any) => console.log(error),
      () => console.log('Done getting users')
    )
  }

  onCreateUser(): void {
    this.userService.createUser(this.user).subscribe(
      (response) => console.log(response),
      (error: any) => console.log(error),
      () => console.log('Done creating users')
    )
  }

  onUpdateUser(): void {
    this.userService.updateUser(this.user).subscribe(
      (response) => console.log(response),
      (error: any) => console.log(error),
      () => console.log('Done update users')
    )
  }

  onPatchUser(): void {
    this.userService.patchUser(this.user1).subscribe(
      (response) => console.log(response),
      (error: any) => console.log(error),
      () => console.log('Done patching users')
    )
  }
  onDeleteUser(): void {
    this.userService.deleteUser(5).subscribe(
      (response) => console.log('Response from delete: ', response),
      (error: any) => console.log(error),
      () => console.log('Done deleting users')
    )
  }
}
